getApp();

Component({
    properties: {
        images: Array,
        more: String,
        loading: Boolean,
        boxShadowColor: {
            type: String,
            value: "gray"
        },
        titleBoxColor: {
            type: String,
            value: "#40d8b2"
        },
        titleColor: {
            type: String,
            value: "#000"
        },
        title: {
            type: String,
            value: "最新图片"
        },
        loadingText: {
            type: String,
            value: "小鱼玩命加载中..."
        }
    },
    data: {},
    lifetimes: {
        attached: function() {},
        detached: function() {}
    },
    methods: {
        clickMore: function(t) {
            wx.navigateTo({
                url: this.properties.more
            });
        },
        clickImage: function(t) {
            console.log(t), wx.navigateTo({
                url: "/pages/picture/detail?id=".concat(t.currentTarget.dataset.id, "&imgSrc=").concat(t.currentTarget.dataset.src)
            });
        },
        outputSrc: function(t) {
            console.log(t.currentTarget.dataset.src);
        }
    }
});