// pages/mine/mine.js
Page({

  /**
   * 页面的初始数据
   */
  data: {
    active:0,
    list:[{
      icon:"home-o",
      text:"首页",
      pagePath:"/pages/index/index"
    },
    {
      icon:"like-o",
      text:"社区",
      pagePath:"/pages/social/social"
    },
    {
      icon:"shopping-cart-o",
      text:"购物",
      pagePath:"/pages/shop/shop"
    },
    {
      icon:"user-o",
      text:"我的",
      pagePath:"/pages/mine/mine"
    }
  ]

  },
  //点击底部导航栏的切换事件
  onChange(event) {
    const self=this;//this是当前页面的指引
    this.setData({ active: event.detail });
    wx.switchTab({
      url: self.data.list[event.detail].pagePath,
    })//即使在这里有路径在app.json里还是要对tabbar配置路径
   
  },

  /**
   * 生命周期函数--监听页面加载
   */
  onLoad(options) {

  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady() {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow() {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide() {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload() {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh() {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom() {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage() {

  }
})